-- Migration script to convert smart locks database to wall art database
USE smart_locks_ecommerce;

-- First, backup existing data
CREATE TABLE IF NOT EXISTS products_backup AS SELECT * FROM products;

-- Drop existing products table
DROP TABLE IF EXISTS products;

-- Recreate products table with new schema
CREATE TABLE products (
  id INT PRIMARY KEY AUTO_INCREMENT,
  name VARCHAR(255) NOT NULL,
  description TEXT NOT NULL,
  detailedDescription JSON,
  price DECIMAL(10, 2) NOT NULL,
  stock INT NOT NULL DEFAULT 0,
  images JSON NOT NULL,
  features JSON NOT NULL,
  specifications JSON NOT NULL,
  category ENUM('anime', 'abstract', 'nature', 'custom', 'geometric', 'portrait', 'other') NOT NULL,
  isActive BOOLEAN DEFAULT TRUE,
  isOnSale BOOLEAN DEFAULT FALSE,
  salePrice DECIMAL(10, 2),
  createdAt TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  updatedAt TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
);

-- Recreate indexes
CREATE INDEX idx_products_category ON products(category);
CREATE INDEX idx_products_active ON products(isActive);
CREATE INDEX idx_products_price ON products(price);

